import React, { useReducer, useEffect } from "react";

import Header from './components/Header';
import Recipe from './components/Recipe';
import Search from  './components/Search';
import spinner from "./assets/ajax-loader.gif";
import './App.css';
import { initialState, reducer } from "./redux/reduxer";
import axios from "axios";
const RECIPE_API_URL = "https://www.themealdb.com/api/json/v1/1/search.php?s="; // you should replace this with yours

const App = () => {
  const [state, dispatch] = useReducer(reducer, initialState);

  useEffect(() => {
    axios.get(RECIPE_API_URL).then(jsonResponse => {
      dispatch({
        type: "SEARCH_RECIPES_SUCCESS",
        payload: jsonResponse.data.Search
      });
    });
  }, []);

  // you can add this to the onClick listener of the Header component
  const refreshPage = () => {
    window.location.reload();
  };

  const search = searchValue => {
    dispatch({
      type: "SEARCH_RECIPES_REQUEST"
    });

    axios(`https://www.themealdb.com/api/json/v1/1/search.php?s=${searchValue}`).then(
      jsonResponse => {
        if (jsonResponse.data.Response === "True") {
          dispatch({
            type: "SEARCH_RECIPES_SUCCESS",
            payload: jsonResponse.data.Search
          });
        } else {
          dispatch({
            type: "SEARCH_RECIPES_FAILURE",
            error: jsonResponse.data.Error
          });
        }
      }
    );
  };

  const { recipes, errorMessage, loading } = state;

  const retrievedRecipes =
    loading && !errorMessage ? (
      <img className="spinner" src={spinner} alt="Loading spinner" />
    ) : errorMessage ? (
      <div className="errorMessage">{errorMessage}</div>
    ) : (
      recipes.map((recipe, index) => (
        <Recipe key={`${recipe.strMeal}`} recipe={recipe} />
      ))
    );

  return (
    <div className="App">
      <div className="m-container">
        <Header text="HOOKED" />

        <Search search={search} />

        <p className="App-intro">Sharing a few of our favourite recipes</p>

        <div className="recipes">{retrievedRecipes}</div>
      </div>
    </div>
  );
};

export default App;
