import React from "react";
import { connect } from 'react-redux';
import { withRouter,useHistory  } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button
  } from 'reactstrap';
const RecipeList = ({ loadingP, recipesP }) => {
    const history = useHistory();
    console.log("recipesP",recipesP);
    const _onClick = id => {
        console.log("test :", id);
        history.push({ pathname: "/detail", state: { recipeId: id } });

    }
    if (loadingP === true) {
        return (
            <div>
                <p>loading</p>
            </div>
        );
    }

    return (
        <div >
        {recipesP.map(item => (
             <div  key={item._id} onClick={() => _onClick(item._id)}>   
                <Card style={{ width: '18rem' }}>
                <Card.Img variant="top" src={item.response.imgLink} />
                <Card.Body>
                <Card.Title>{item.response.title}</Card.Title>
                <Card.Text>
                {item.response.category}
                </Card.Text>
                
            </Card.Body>
            </Card>
        
                </div>
            ))}
            

        </div>
    );
}
const mapStatetoProps = state => {
    return {
        //comp ne ilgilendiriyorsa onu bağlamamı gerekiyor 
        loadingP: state.app.loading,
        recipesP: state.app.recipes,

    }
}
export default withRouter(connect(mapStatetoProps)(RecipeList));
