import React from "react";
import { connect } from 'react-redux';
import { withRouter,useHistory  } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.css';
const RecipeList = ({ loadingP, recipesP }) => {
    const history = useHistory();
    console.log("recipesP",recipesP);
    const _onClick = id => {
        console.log("test :", id);
        history.push({ pathname: "/detail", state: { recipeId: id } });

    }
    if (loadingP === true) {
        return (
            <div>
                <p>loading</p>
            </div>
        );
    }

    return (
        
        <div className="container">
           {recipesP.map(item => (
                <div  className="card mb-3" key={item._id} onClick={() => _onClick(item._id)}>           
                <div className="row no-gutters">
                        <div className="col-md-4">
                            <img src={item.response.imgLink} alt="Card Image" className="card-img"></img>
                        </div>
                        <div className="col-md-8">
                            <div className="card-body">
                                <h5 className="card-title">{item.response.title}</h5>
                                <p className="card-text">{item.response.category}</p>
                                 </div>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}
const mapStatetoProps = state => {
    return {
        //comp ne ilgilendiriyorsa onu bağlamamı gerekiyor 
        loadingP: state.app.loading,
        recipesP: state.app.recipes,

    }
}
export default withRouter(connect(mapStatetoProps)(RecipeList));
