const initialState = {
    recipes: [],
    loading: false,
}
export default (state = initialState, action) => {
    switch (action.type) {
        case 'recipe/request':
            return {
                ...state,
                loading: true
            };
        case 'recipe/success':
            return {
                ...state,
                recipes: action.data,
                loading: false
            };
        default:
            return state;
    }
}
