import React, { Fragment, useState, useEffect } from "react";
import { useDispatch } from 'react-redux';
import 'bootstrap/dist/css/bootstrap.css';
const SearchBar = () => {
    const [currentValue, setCurrentValue] = useState("");

    
    const dispatch = useDispatch();
    const _onClick = async () => {
        
        dispatch({ type: "recipe/request" });
        try {

            const result = await fetch(`http://localhost:3000/recipes=${currentValue}`);
            const res = await result.json();
            console.log("res", res.meals[0]);
            
        dispatch({ type: "recipe/success", data: res.meals });
        } catch (error) {
            alert("Opps!");
        }

    };
    useEffect(() => {
        async function fetchData() {
            dispatch({ type: "recipe/request" });
            try {
    
                const result = await fetch(`http://localhost:3000/recipe`);
                const res = await result.json();
                console.log("res", res.meals[0]);
                
            dispatch({ type: "recipe/success", data: res.meals });
            } catch (error) {
                alert("Opps!");
            }
        }
    fetchData();
    },[dispatch])
    
    const _onChange = event => {
        setCurrentValue(event.target.value);
    };
    return (
        <Fragment>
            <i class="fas fa-search" aria-hidden="true"></i>
            <input class="form-control" type="text" onChange={_onChange} />
            <button class="btn btn-info" onClick={_onClick} >Search</button>
        </Fragment>
    );
};



export default SearchBar;
