import React from 'react';
import { connect } from 'react-redux';
import { withRouter, useHistory } from 'react-router-dom';

const RecipeList = ({ selectedCategory }) => {
    const history = useHistory();
    const _onClick = id => {
        history.push({ pathname: '/detail', state: { recipeId: id } });  }

    return (
        <div class="card" >
           {recipesP.map(item => (
                <div onClick={() => _onClick(item._id)} key={item._id}>           
                <img className="card-img-top" src={item.response.imgLink} alt="Card image" />
                <h4 className="card-title" >{item.response.title}</h4>
                <h5 className="card-text" >Category:{item.response.category}</h5>
                </div>
            ))}
            

        </div>
    );
}

const mapStateToProps = (state) => {
    return {
        selectedCategory: state.app.selectedRecipes
    };
}


export default withRouter(connect(mapStateToProps)(RecipeList));
