import React from 'react';
import { connect, useSelector } from 'react-redux';
import { withRouter, Link } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.css';
const RecipeDetail = ({ location }) => {


    const recipeList = useSelector(state => state.app.recipes);
   
    const selectedRecipe = recipeList.filter(item => {
        if (item.idMeal === location.state.recipeId) { return true; }
        return false;
    }
    );
    console.log(selectedRecipe);
    if (selectedRecipe.length <= 0) {
        return <Link to="/">LÃ¼tfen Ansayfaya Gidiniz</Link>
    }
    
    return (
        <div class="card" >
            <img className="card-img-top" src={selectedRecipe[0].strMealThumb} alt="Card image" />
            <h2>{selectedRecipe[0].strMeal}</h2>
            <h5>Category:{selectedRecipe[0].strCategory}</h5>
            <h5>{selectedRecipe[0].strArea}</h5>
            <ul class="list">
               
            <li> {selectedRecipe[0].strIngredient1} </li>
                <li> {selectedRecipe[0].strIngredient2} </li>
                <li> {selectedRecipe[0].strIngredient3} </li>
                <li> {selectedRecipe[0].strIngredient4} </li>
                <li> {selectedRecipe[0].strIngredient5} </li>
                <li> {selectedRecipe[0].strIngredient6} </li>
                <li> {selectedRecipe[0].strIngredient7} </li>
                <li> {selectedRecipe[0].strIngredient8} </li>
                <li> {selectedRecipe[0].strIngredient9} </li>
                <li> {selectedRecipe[0].strIngredient10} </li>
                <li> {selectedRecipe[0].strIngredient11} </li>
                <li> {selectedRecipe[0].strIngredient12} </li>
                <li> {selectedRecipe[0].strIngredient13} </li>
                <li> {selectedRecipe[0].strIngredient14} </li>
                <li> {selectedRecipe[0].strIngredient15} </li>
                <li> {selectedRecipe[0].strIngredient16} </li>
                <li> {selectedRecipe[0].strIngredient17} </li>
                <li> {selectedRecipe[0].strIngredient18} </li>
                <li> {selectedRecipe[0].strIngredient19} </li>
                <li> {selectedRecipe[0].strIngredient20} </li>
               
                </ul>
               


            
              <p> {selectedRecipe[0].strInstructions}</p>

    </div>);

};


export default withRouter(RecipeDetail);
