import React from "react";


const DEFAULT_PLACEHOLDER_IMAGE =
"https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQPZacxRcb_1S9_aYaOTxKSYXk6UPhT9TXFP7AjIN6-d7DdJJjo";


const Recipe = ({ recipe }) => {
  const image =
    recipe.Image === "N/A" ? DEFAULT_PLACEHOLDER_IMAGE : recipe.strMealThumb;
  return (
    <div className="recipe">
      <h2>{recipe.strMeal}</h2>
      <div>
        <img
          width="200"
          alt={`The recipe titled: ${recipe.strMeal}`}
          src={image}
        />
      </div>
      <p>({recipe.strCategory})</p>
    </div>
  );
};


export default Recipe;
