import React from "react";
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.css';
const RecipeList = ({  recipesP, history }) => {
    
    console.log("recipesP",recipesP);
    const _onClick = id => {
        console.log("test :", id);
        history.push({ pathname: "/detay", state: { recipeId: id } });

    };
  

    return (
        <div class="card" >
           {recipesP.map(item => (
                <div onClick={() => _onClick(item._id)} key={item._id}>           
                <img className="card-img-top" src={item.response.imgLink} alt="Card image" />
                <h4>{item.response.title}</h4>
                <h5>Category:{item.response.category}</h5>
                </div>
            ))}
            

        </div>
    );
}
const mapStatetoProps = state => {
    return {
        //comp ne ilgilendiriyorsa onu bağlamamı gerekiyor 
      
        recipesP: state.app.recipes,

    }
}
export default withRouter(connect(mapStatetoProps)(RecipeList));
